// src/WorkerDashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const WorkerDashboard = () => {
    const [workerId, setWorkerId] = useState(''); // Replace with actual worker ID logic
    const [walletInfo, setWalletInfo] = useState(null);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchWalletInfo = async () => {
            try {
                const response = await axios.get('/checkWallet', { params: { worker_id: workerId } });
                setWalletInfo(response.data);
            } catch (err) {
                setError('Error fetching wallet information.');
                console.error('Error:', err);
            }
        };

        if (workerId) {
            fetchWalletInfo();
        }
    }, [workerId]);

    const handleWorkerIdChange = (event) => {
        setWorkerId(event.target.value);
    };

    const handleFetchWalletInfo = () => {
        if (workerId) {
            fetchWalletInfo();
        }
    };

    return (
        <div>
            <h1>Worker Dashboard</h1>

            <div>
                <label htmlFor="workerId">Enter Worker ID:</label>
                <input
                    id="workerId"
                    type="text"
                    value={workerId}
                    onChange={handleWorkerIdChange}
                    placeholder="Worker ID"
                />
                <button onClick={handleFetchWalletInfo}>Fetch Wallet Info</button>
            </div>

            {error && <p style={{ color: 'red' }}>{error}</p>}

            {walletInfo ? (
                <section>
                    <h2>Wallet Information</h2>
                    <p><strong>Wallet Address:</strong> {walletInfo.wallet}</p>
                    <p><strong>Balance:</strong> {walletInfo.balance} ETH</p>
                </section>
            ) : (
                <p>Enter a Worker ID and click "Fetch Wallet Info" to see details.</p>
            )}
        </div>
    );
};

export default WorkerDashboard;
