import { ethers } from "ethers";
import TaskAllocationAbi from "../../build/contracts/TaskAllocation.json"; // ABI file from Truffle

const TASK_ALLOCATION_ADDRESS = "Your_Deployed_Contract_Address"; // Replace with your deployed contract address

export const getTaskAllocationContract = () => {
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner();
    const taskAllocationContract = new ethers.Contract(
        TASK_ALLOCATION_ADDRESS,
        TaskAllocationAbi,
        signer
    );
    return taskAllocationContract;
};
